drop table passe;
drop table salle;
drop table distribution;
drop table acteur;
drop table film;
drop table personne;
drop table cinema;
create table personne( nump INT PRIMARY KEY NOT NULL, prenom VARCHAR(100), nom VARCHAR(100), date_naissance date, nationalite VARCHAR(50));
create table film( numf INT PRIMARY KEY NOT NULL, titre VARCHAR(200), genre VARCHAR(50), année INT, longueur VARCHAR(5), budget INT, realisateur INT, salaire_real INT, FOREIGN KEY (realisateur) REFERENCES Personne(nump));
create table acteur( numa INT PRIMARY KEY NOT NULL, agent INT, specialite VARCHAR(50), taille FLOAT, poids INT, FOREIGN KEY (numa) REFERENCES Personne(nump), FOREIGN KEY (agent) REFERENCES Personne(nump));
create table distribution( numf INT, numa INT, role VARCHAR(100), salaire INT, primary key (numf,numa,role),FOREIGN KEY (numa) REFERENCES ACTEUR(numa), FOREIGN KEY (numf) REFERENCES Film(numf) );
create table cinema( numc INT PRIMARY KEY NOT NULL, nom VARCHAR(50), adresse VARCHAR(255), ville VARCHAR(200), telephone VARCHAR(10), compagnie VARCHAR(50));
create table salle( numc INT, nums INT, taille_ecran INT, année INT, nb_place INT, primary key (numc,nums), FOREIGN KEY (numc) REFERENCES Cinema(numc));
create table passe( numf INT, numc INT, nums INT, date_deb date, date_fin date, horaire varchar(5), prix INT, primary key(numf,numc,nums,date_deb, horaire), FOREIGN KEY (numf) REFERENCES Film(numf), FOREIGN KEY (numc) REFERENCES Cinema(numc) , FOREIGN KEY (numc,nums) REFERENCES Salle(numc,nums));
